function Speed(a,b)local c=game:GetService("Players").LocalPlayer.Character:FindFirstChild("Humanoid")local d=tonumber(a)if d and b==true or b==nil then c.WalkSpeed=d elseif b==false then c.WalkSpeed=16 elseif d==nil then warn("The supplied value is not a valid number.")end end;function JPower(a,b)local c=game:GetService("Players").LocalPlayer.Character:FindFirstChild("Humanoid")local d=tonumber(a)if d and b==true or b==nil then c.JumpPower=d elseif b==false then c.JumpPower=50 elseif d==nil then warn("The supplied value is not a valid number.")end end
